var assert = require('assert');
var Heartbeat = require('./heartbeat.js');

var emitter = Heartbeat.start({
    nodes: [
      {address: 'localhost'}
    ],
  }, function(emitter) {
    emitter.on('heartbeat', function(message, rinfo) {
      assert.equal(rinfo.address, '127.0.0.1');
      console.log('hearbeat success');
      process.exit(0);
    });
  }
);
