var assert = require('assert');
var Heartbeat = require('./heartbeat.js');

var emitter = Heartbeat.start({
    nodes: [
      {address: 'localhost'}
    ],
  }, function(emitter) {
    emitter.on('ping', function(node) {
      assert.equal(node.address, 'localhost');
      console.log('ping captured');
      process.exit(0);
    });
  } 
);
